mapper.update:
UPDATE member 
SET name=#{name}, birth_date=#{birth_date}, phone=#{phone}, email=#{email}, address=#{address}, location=#{location}
WHERE member_id=#{member_id}

mapper.select:
SELECT *
FROM (
    SELECT alarm_idx, alarm_content, reg_date, ischeck, member_id, code_num, ROW_NUMBER() OVER(ORDER BY reg_date DESC) AS rnum
    FROM alarm 
    WHERE member_id=#{param1}
    )
WHERE rnum BETWEEN #{param2} AND #{param3}

SELECT *
FROM (
    SELECT b.block_idx, b.target_id, b.member_id, p.newfilename, ROW_NUMBER() OVER(ORDER BY b.block_idx) AS rnum
    FROM block b, profilefile p
    WHERE b.member_id = p.member_id AND b.member_id=#{param1}
    )
WHERE rnum BETWEEN #{param2} AND #{param3}
